mapper.insert:
INSERT
  INTO TBL_USER A
  (
    A.USER_NO
  , A.USER_NAME
  , A.USER_EMAIL
  , A.USER_BIRTH
  , A.USER_PHONE
  , A.USER_BUY_TICKET_YN
  , A.USER_CATEGORY_NO
  , A.USER_TYPE
  )
  VALUES
  (
    SEQ_USER_NO.NEXTVAL
  , #{ userName }
  , #{ userEmail }
  , #{ userBirth }
  , #{ userPhone }
  , 'N'
  , 2
  , #{ userType }
  )

mapper.update:
UPDATE
       TBL_USER A
   SET A.USER_NAME = #{ userName }
     , A.USER_PHONE = #{ userPhone }
 WHERE A.USER_EMAIL = #{ userEmail }
